package com.globallogic;


//Write a program to find the first and the last occurence of the letter 'o' and character ',' in "Hello, World".

public class Occurence {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s = "Hello, World";
		System.out.println("first occurence of o is at index value: "+s.indexOf('o'));
		System.out.println("first occurence of o is at index value: "+s.indexOf(','));
		System.out.println("first occurence of o is at index value: "+s.lastIndexOf('o'));
		

	}

}
